	{
		Console.WriteLine ("Hello World!");
	}
}

class ClassDeclaration { 
	public void TestMethod ()
	{
		try {
			TestMethod ("");
		} catch (Exception e) {
			// Do something
		} finally {
			// Do something
		}
	}

	public void TestMethod (string test)
	{
		lock (this) {
			switch (test) {
				case "A":
					Console.WriteLine ("was A");
					break;
				case "B":
					Console.WriteLine ("was B");
					break;
			}
		}
	}

	public void Calculate (int a, int b)
	{
		if (a < b) {
			for (int i = a; i < b; i++) {
				Console.WriteLine (i);
			}
		} else {
			using (object o = new object ()) {
				while (b < a) {
					Console.WriteLine (b++);
				}
			}
		}
	}
}

class ClassDeclaration { 
	public void TestMethod ()
	{
		int a = 5 << 5;
		int b = (a + 5 - 3) * 6 / 2;
		a += b;
		a = a & ~255;
		if (a == b || b < a >> 1) {
			b -= a;
		}
	}
}

class ClassDeclaration { 
	public string GetSign (int i)
	{
		return i < 0 ? "-" : "+";
	}
}

class ClassDeclaration { 
	public void Test (int i)
	{
		switch (i) {
			case 0:
				Console.WriteLine ("was zero");
				break;
			case 1:
				Console.WriteLine ("was one");
				break;
			default:
				Console.WriteLine ("was " + i);
				break;
		}
	}
}

class ClassDeclaration { 
		public void Test (int i)
		{
			if (i == 5) {
				Console.WriteLine ("== 5");
			} else if (i > 0) {
				Console.WriteLine (">0");
			} else if (i < 0) {
				Console.WriteLine ("<0");
			} else {
				Console.WriteLine ("== 0");
			}
		}
	}

